INSERT IGNORE INTO `wp_wj2mzjsn96_posts` VALUES (5, 1, '2015-10-07 02:31:00', '2015-10-07 02:31:00', '<h1>Integrative Wisdom is a series of candid interviews that reveal insights and stories from the industry’s pioneers.</h1><p>Whether you are a <a href="/videos/for-students">student</a> working towards a degree in medicine, a <a href="/videos/for-patients">patient</a> looking for a better approach to health, or a <a href="/videos/for-practitioners">practitioner</a> in the field, these short videos will illuminate the way to Integrative health.  </p>', 'Integrative Wisdom', '', 'publish', 'closed', 'closed', '', 'home-page', '', '', '2015-11-17 09:25:54', '2015-11-17 15:25:54', '', 0, 'http://intwis.klosenuf.com/?page_id=5', 0, 'page', '', 0); 
