INSERT IGNORE INTO `wp_wj2mzjsn96_posts` VALUES (126, 1, '2015-10-30 22:56:52', '2015-10-31 03:56:52', '<strong>We are in the midst of a special moment.</strong>\r\n\r\nPeople are taking control of their health like never before.\r\n\r\nThe integrative care industry is rapidly expanding.\r\n\r\nNew students, patients and practitioners are joining the movement every day.\r\n\r\n&nbsp;\r\n\r\n<strong>As industry leaders, we must embrace a bigger role—a higher calling.</strong>\r\n\r\nWe can’t narrowly focus on product development or sales.\r\n\r\nWe must seek out knowledge and share it.\r\n\r\nWe must become Stewards of The Truth.\r\n\r\n&nbsp;\r\n\r\n<strong>Our wisdom will inspire and embolden a new generation of leaders.</strong>\r\n\r\nYou could be one of those leaders.\r\n\r\nYou could break down barriers and chart a new course.\r\n\r\nYou could help people live happier and longer lives.\r\n\r\n<strong> </strong>\r\n\r\n<strong>Let’s give everyone access to the knowledge they needs to choose an ideal path towards healthy living. </strong>', 'Why Wisdom Matters', '', 'inherit', 'closed', 'closed', '', '9-revision-v1', '', '', '2015-10-30 22:56:52', '2015-10-31 03:56:52', '', 9, 'http://intwis.klosenuf.com/9-revision-v1/', 0, 'revision', '', 0); 
